// ðŸ§  Advanced Query Processor for Fashion Search
// Processes search queries with color, style, and context detection

console.log('=== SMART SEARCH QUERY PROCESSOR START ===');

// Initialize default values
let inputData = {};
let searchQuery = '';
let userId = 'anonymous';
let filters = {};
let maxResults = 20;
let offset = 0;
let searchType = 'text';
let imageUri = '';

try {
  // Extract input data from webhook
  const inputs = $input.all();
  if (inputs.length > 0 && inputs[0] && inputs[0].json) {
    inputData = inputs[0].json;
    console.log('âœ… Input received:', Object.keys(inputData));

    // Extract parameters
    searchQuery = inputData.query || inputData.searchQuery || inputData.q || '';
    userId = inputData.userId || inputData.user_id || 'anonymous';
    filters = inputData.filters || {};
    maxResults = Math.max(1, Math.min(50, parseInt(inputData.limit || inputData.maxResults) || 20));
    offset = Math.max(0, parseInt(inputData.offset) || 0);
    searchType = inputData.searchType || 'text';
    imageUri = inputData.imageUri || '';

    console.log('ðŸ” Extracted params:', {
      query: searchQuery,
      userId,
      maxResults,
      filters,
      searchType,
      imageUri
    });
  }
} catch (error) {
  console.log('âŒ Error processing input:', error.message);
}

// Enhanced color detection with fashion-specific intelligence
function analyzeQuery(query) {
  const original = String(query).toLowerCase().trim();

  // Enhanced color detection
  const colorMap = {
    'black': ['black', 'noir', 'dark', 'charcoal', 'ebony', 'jet', 'midnight'],
    'white': ['white', 'cream', 'ivory', 'off-white', 'pearl', 'snow', 'vanilla'],
    'red': ['red', 'crimson', 'burgundy', 'maroon', 'cherry', 'rose', 'scarlet', 'ruby'],
    'blue': ['blue', 'navy', 'royal', 'cobalt', 'azure', 'indigo', 'sapphire', 'denim'],
    'green': ['green', 'emerald', 'forest', 'olive', 'mint', 'sage', 'lime', 'teal'],
    'yellow': ['yellow', 'gold', 'mustard', 'amber', 'lemon', 'sunny', 'canary'],
    'purple': ['purple', 'violet', 'lavender', 'plum', 'orchid', 'amethyst', 'magenta'],
    'pink': ['pink', 'rose', 'blush', 'fuchsia', 'magenta', 'coral', 'salmon'],
    'orange': ['orange', 'coral', 'peach', 'tangerine', 'apricot', 'rust', 'copper'],
    'brown': ['brown', 'tan', 'beige', 'camel', 'coffee', 'chocolate', 'khaki', 'taupe'],
    'gray': ['gray', 'grey', 'silver', 'charcoal', 'slate', 'ash', 'pewter'],
    'neutral': ['neutral', 'nude', 'natural', 'beige', 'taupe', 'sand', 'cream']
  };

  let detectedColors = [];
  for (const [color, variations] of Object.entries(colorMap)) {
    if (variations.some(variation => original.includes(variation))) {
      detectedColors.push(color);
    }
  }

  // Category detection with fashion-specific terms
  let category = 'general';
  const categoryMap = {
    'dresses': ['dress', 'gown', 'frock', 'sundress', 'maxi', 'midi', 'mini', 'cocktail', 'evening'],
    'tops': ['shirt', 'top', 'blouse', 'tee', 't-shirt', 'tank', 'camisole', 'sweater', 'cardigan', 'hoodie', 'pullover'],
    'bottoms': ['pants', 'jeans', 'trouser', 'shorts', 'skirt', 'leggings', 'joggers', 'chinos', 'capris'],
    'shoes': ['shoe', 'boot', 'sneaker', 'sandal', 'heel', 'flat', 'loafer', 'pump', 'oxford', 'mule'],
    'outerwear': ['jacket', 'coat', 'blazer', 'vest', 'parka', 'trench', 'bomber', 'cardigan', 'wrap'],
    'accessories': ['bag', 'purse', 'wallet', 'belt', 'hat', 'scarf', 'jewelry', 'watch', 'sunglasses', 'earrings']
  };

  for (const [cat, keywords] of Object.entries(categoryMap)) {
    if (keywords.some(keyword => original.includes(keyword))) {
      category = cat;
      break;
    }
  }

  // Style and occasion detection
  let style = 'general';
  const styleMap = {
    'casual': ['casual', 'relaxed', 'everyday', 'comfortable', 'laid-back', 'weekend'],
    'formal': ['formal', 'business', 'professional', 'office', 'work', 'corporate', 'suit'],
    'party': ['party', 'evening', 'cocktail', 'night out', 'celebration', 'festive', 'club'],
    'summer': ['summer', 'beach', 'vacation', 'light', 'breathable', 'sunny', 'tropical'],
    'winter': ['winter', 'warm', 'cozy', 'thermal', 'layering', 'cold', 'wool', 'fleece'],
    'vintage': ['vintage', 'retro', 'classic', 'timeless', 'traditional', 'old-school', 'antique'],
    'modern': ['modern', 'contemporary', 'trendy', 'current', 'fashionable', 'latest', 'new'],
    'bohemian': ['boho', 'bohemian', 'free-spirited', 'artsy', 'eclectic', 'hippie', 'flowy'],
    'athletic': ['athletic', 'sporty', 'gym', 'workout', 'fitness', 'active', 'performance'],
    'elegant': ['elegant', 'sophisticated', 'classy', 'refined', 'luxurious', 'graceful']
  };

  for (const [styleType, keywords] of Object.entries(styleMap)) {
    if (keywords.some(keyword => original.includes(keyword))) {
      style = styleType;
      break;
    }
  }

  // Price range detection
  let priceRange = null;
  if (original.includes('cheap') || original.includes('budget') || original.includes('affordable')) {
    priceRange = { min: 0, max: 50 };
  } else if (original.includes('expensive') || original.includes('luxury') || original.includes('premium')) {
    priceRange = { min: 200, max: 1000 };
  } else if (original.includes('mid-range') || original.includes('moderate')) {
    priceRange = { min: 50, max: 200 };
  }

  // Brand detection
  const brandKeywords = ['nike', 'adidas', 'zara', 'h&m', 'gucci', 'prada', 'levi', 'calvin klein', 'tommy hilfiger'];
  let detectedBrand = null;
  for (const brand of brandKeywords) {
    if (original.includes(brand)) {
      detectedBrand = brand;
      break;
    }
  }

  // Create enhanced search terms
  const enhancedTerms = [original];
  if (detectedColors.length > 0) enhancedTerms.push(...detectedColors);
  if (category !== 'general') enhancedTerms.push(category);
  if (style !== 'general') enhancedTerms.push(style);
  if (detectedBrand) enhancedTerms.push(detectedBrand);

  // Add general fashion terms
  enhancedTerms.push('fashion', 'clothing', 'style', 'outfit');

  return {
    original: query.trim(),
    expanded: [...new Set(enhancedTerms)].join(' '),
    colors: detectedColors,
    category: category,
    style: style,
    priceRange: priceRange,
    brand: detectedBrand,
    searchTerms: enhancedTerms,
    intent: determineSearchIntent(original)
  };
}

// Determine user search intent
function determineSearchIntent(query) {
  if (query.includes('similar') || query.includes('like this')) return 'similar';
  if (query.includes('outfit') || query.includes('look')) return 'complete_outfit';
  if (query.includes('match') || query.includes('goes with')) return 'coordination';
  if (query.includes('trend') || query.includes('popular')) return 'trending';
  if (query.includes('occasion') || query.includes('event')) return 'occasion_based';
  if (query.includes('buy') || query.includes('purchase') || query.includes('shop')) return 'shopping';
  return 'general_search';
}

// Process the query
let queryAnalysis = {};
if (searchType === 'image' && imageUri) {
  queryAnalysis = {
    original: 'image_search',
    expanded: 'image search visual similarity fashion clothing',
    colors: [],
    category: 'general',
    style: 'general',
    priceRange: null,
    brand: null,
    searchTerms: ['image', 'search', 'visual', 'similarity', 'fashion'],
    intent: 'image_search'
  };
} else {
  queryAnalysis = analyzeQuery(searchQuery);
}

// Merge with provided filters
const allColors = [...new Set([...queryAnalysis.colors, ...(filters.colors || [])])];
const finalPriceRange = filters.priceRange || queryAnalysis.priceRange;
const finalCategories = filters.categories || [queryAnalysis.category].filter(c => c !== 'general');

// Build comprehensive search data
const result = {
  userId: userId,
  searchQuery: {
    ...queryAnalysis,
    colors: allColors
  },
  filters: {
    ...filters,
    colors: allColors,
    categories: finalCategories,
    priceRange: finalPriceRange,
    brands: filters.brands || (queryAnalysis.brand ? [queryAnalysis.brand] : []),
    source: filters.source || 'all'
  },
  searchType: searchType,
  imageUri: imageUri,
  maxResults: maxResults,
  offset: offset,
  timestamp: new Date().toISOString(),
  searchId: `search_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
  debug: {
    originalInput: inputData,
    detectedColors: queryAnalysis.colors,
    detectedCategory: queryAnalysis.category,
    detectedStyle: queryAnalysis.style,
    searchIntent: queryAnalysis.intent,
    enhancedQuery: queryAnalysis.expanded,
    searchType: searchType
  }
};

console.log('ðŸŽ¯ Final processed data:', JSON.stringify(result, null, 2));
console.log('=== SMART SEARCH QUERY PROCESSOR END ===');

return result;

