// ðŸŽ¯ Smart Results Aggregator
// Combines database, web, and image search results

console.log('=== RESULTS AGGREGATOR START ===');

const allInputs = $input.all();
let queryData = null;
let dbResults = [];
let webResults = [];
let imageAnalysis = null;

// Process inputs from different sources
allInputs.forEach((input, index) => {
  const data = input.json;
  console.log(`Processing input ${index}:`, typeof data, Object.keys(data || {}));

  // Query data (from query processor)
  if (data && data.searchQuery && data.userId) {
    queryData = data;
    console.log('âœ… Found query data:', queryData.searchId);
  }
  // Database results (from Supabase)
  else if (Array.isArray(data)) {
    dbResults = data.map((item, idx) => {
      const images = Array.isArray(item.images) ? item.images : (item.images ? [item.images] : []);
      return {
        id: item.id || `db_${idx}`,
        title: item.name || 'Fashion Item',
        description: item.description || '',
        price: item.price ? `$${item.price}` : null,
        originalPrice: item.original_price ? `$${item.original_price}` : null,
        brand: item.brands?.name || 'Brand',
        category: item.categories?.name || 'Fashion',
        colors: item.colors || [],
        tags: item.tags || [],
        images: images,
        image: images[0] || null,
        source: 'database',
        relevanceScore: calculateRelevanceScore(item, queryData),
        discount: item.discount_percentage || (item.original_price && item.price ? 
          Math.round(((item.original_price - item.price) / item.original_price) * 100) : null),
        isFeatured: item.is_featured || false,
        popularityScore: item.popularity_score || 0,
        materials: item.materials || [],
        sizes: item.sizes || [],
        productCode: item.product_code
      };
    });
    console.log(`âœ… Processed ${dbResults.length} database results`);
  }
  // Web search results (from Brave API)
  else if (data && data.web && data.web.results) {
    webResults = data.web.results.map((item, idx) => {
      // Extract price from description or title
      const priceMatch = (item.description + ' ' + item.title).match(/\$([0-9,]+(?:\.[0-9]{2})?)/g);
      const price = priceMatch ? priceMatch[0] : null;

      return {
        id: `web_${idx}`,
        title: item.title || 'Fashion Item',
        description: item.description || '',
        url: item.url || '',
        price: price,
        source: 'web',
        relevanceScore: calculateWebRelevanceScore(item, queryData),
        publishedTime: item.published_time || null,
        thumbnail: item.thumbnail || null,
        image: item.thumbnail || null
      };
    });
    console.log(`âœ… Processed ${webResults.length} web results`);
  }
  // Image analysis results (from OpenAI)
  else if (data && data.choices && data.choices[0]) {
    imageAnalysis = {
      analysis: data.choices[0].message.content,
      model: data.model || 'gpt-4o-mini',
      usage: data.usage || null
    };
    console.log('âœ… Processed image analysis');
  }
});

// Calculate relevance score for database items
function calculateRelevanceScore(item, queryData) {
  if (!queryData) return 50;

  let score = 0;
  const query = queryData.searchQuery;

  // Color matching
  if (query.colors && query.colors.length > 0) {
    const colorMatches = query.colors.filter(color => 
      item.colors && item.colors.includes(color)
    ).length;
    score += colorMatches * 15;
  }

  // Text matching in name and description
  const text = `${item.name || ''} ${item.description || ''}`.toLowerCase();
  const originalQuery = query.original.toLowerCase();
  if (text.includes(originalQuery)) {
    score += 30;
  }

  // Category matching
  if (query.category !== 'general' && item.categories?.name) {
    if (item.categories.name.toLowerCase().includes(query.category)) {
      score += 20;
    }
  }

  // Featured items bonus
  if (item.is_featured) {
    score += 10;
  }

  // Popularity bonus
  score += Math.min(item.popularity_score || 0, 20);

  // Price range matching
  if (query.priceRange && item.price) {
    const price = parseFloat(item.price);
    if (price >= query.priceRange.min && price <= query.priceRange.max) {
      score += 15;
    }
  }

  return Math.min(score, 100);
}

// Calculate relevance score for web results
function calculateWebRelevanceScore(item, queryData) {
  if (!queryData) return 25;

  let score = 0;
  const query = queryData.searchQuery;

  // Title matching
  const title = item.title.toLowerCase();
  const originalQuery = query.original.toLowerCase();
  if (title.includes(originalQuery)) {
    score += 20;
  }

  // Fashion-related keywords
  const fashionKeywords = ['fashion', 'clothing', 'apparel', 'style', 'outfit', 'wear'];
  const fashionMatches = fashionKeywords.filter(keyword => 
    title.includes(keyword) || item.description.toLowerCase().includes(keyword)
  ).length;
  score += fashionMatches * 3;

  // Shopping sites bonus
  const shoppingSites = ['amazon', 'ebay', 'etsy', 'shopify', 'store', 'shop', 'buy'];
  const shoppingMatches = shoppingSites.filter(site => 
    item.url.toLowerCase().includes(site)
  ).length;
  score += shoppingMatches * 5;

  return Math.min(score, 50);
}

// Fallback query data
if (!queryData) {
  queryData = {
    searchQuery: { original: 'fashion', colors: [], category: 'general' },
    userId: 'anonymous',
    searchId: `fallback_${Date.now()}`,
    searchType: 'text',
    maxResults: 20
  };
}

// Combine and sort results
let combinedResults = [...dbResults, ...webResults];

// Sort by relevance score (descending)
combinedResults.sort((a, b) => b.relevanceScore - a.relevanceScore);

// Limit results
const finalResults = combinedResults.slice(0, queryData.maxResults || 20);

// Calculate result statistics
const stats = {
  totalResults: finalResults.length,
  databaseResults: dbResults.length,
  webResults: webResults.length,
  hasImageAnalysis: !!imageAnalysis,
  averageRelevanceScore: finalResults.length > 0 ? 
    Math.round(finalResults.reduce((sum, item) => sum + item.relevanceScore, 0) / finalResults.length) : 0,
  searchSources: ['database', 'web', ...(imageAnalysis ? ['image'] : [])],
  processingTime: new Date().toISOString()
};

// Build final response
const response = {
  success: true,
  query: queryData.searchQuery,
  results: finalResults,
  metadata: {
    ...stats,
    searchId: queryData.searchId,
    userId: queryData.userId,
    searchType: queryData.searchType,
    timestamp: new Date().toISOString()
  },
  imageAnalysis: imageAnalysis,
  suggestions: generateSuggestions(finalResults, queryData)
};

// Generate search suggestions
function generateSuggestions(results, queryData) {
  const suggestions = {
    tryBroaderTerms: [],
    popularSearches: ['dresses', 'casual wear', 'formal attire', 'summer fashion', 'accessories'],
    searchTips: [],
    relatedColors: [],
    relatedCategories: []
  };

  // If few results, suggest broader terms
  if (results.length < 3) {
    suggestions.tryBroaderTerms = [
      'Try using broader terms like "clothing" or "fashion"',
      'Remove specific color or style filters',
      'Search for the general category instead of specific items'
    ];
  }

  // Extract related colors from results
  const colorCounts = {};
  results.forEach(item => {
    if (item.colors) {
      item.colors.forEach(color => {
        colorCounts[color] = (colorCounts[color] || 0) + 1;
      });
    }
  });

  suggestions.relatedColors = Object.entries(colorCounts)
    .sort((a, b) => b[1] - a[1])
    .slice(0, 5)
    .map(([color]) => color);

  // Generate search tips based on query
  if (queryData.searchType === 'image') {
    suggestions.searchTips = [
      'Make sure the image is clear and well-lit',
      'Focus on a single clothing item for better results',
      'Try different angles or lighting for the same item'
    ];
  } else {
    suggestions.searchTips = [
      'Use specific color names for better matches',
      'Try combining style with occasion (e.g., "casual summer dress")',
      'Include brand names if you have a preference'
    ];
  }

  return suggestions;
}

console.log('ðŸ“Š Final response stats:', {
  totalResults: response.results.length,
  databaseResults: response.metadata.databaseResults,
  webResults: response.metadata.webResults,
  hasImageAnalysis: response.metadata.hasImageAnalysis,
  averageRelevanceScore: response.metadata.averageRelevanceScore
});

console.log('=== RESULTS AGGREGATOR END ===');

return response;
