// ðŸŽ¯ AI Stylist Message Processor
// Processes user messages and extracts fashion context

console.log('=== AI STYLIST MESSAGE PROCESSOR START ===');

// Initialize variables
let inputData = {};
let userMessage = '';
let userId = 'anonymous';
let sessionId = null;

try {
  const inputs = $input.all();
  if (inputs.length > 0 && inputs[0] && inputs[0].json) {
    inputData = inputs[0].json;
    userMessage = inputData.message || inputData.query || inputData.text || '';
    userId = inputData.userId || inputData.user_id || 'anonymous';
    sessionId = inputData.sessionId || `session_${Date.now()}`;
  }
} catch (error) {
  console.log('âŒ Error processing input:', error.message);
}

// Analyze user message for fashion intent
function analyzeMessage(message) {
  const text = String(message).toLowerCase().trim();

  // Intent classification
  let intent = 'general_chat';
  const intentPatterns = {
    'style_advice': ['style', 'look', 'outfit', 'fashion', 'dress', 'wear'],
    'product_search': ['find', 'search', 'show me', 'looking for', 'need'],
    'color_advice': ['color', 'colour', 'shade', 'palette'],
    'occasion_styling': ['wedding', 'party', 'work', 'date', 'casual', 'formal']
  };

  for (const [category, patterns] of Object.entries(intentPatterns)) {
    if (patterns.some(pattern => text.includes(pattern))) {
      intent = category;
      break;
    }
  }

  // Extract entities
  const colors = ['black', 'white', 'red', 'blue', 'green', 'yellow', 'purple', 'pink'];
  const garments = ['dress', 'shirt', 'pants', 'jeans', 'skirt', 'top', 'jacket', 'shoes'];

  const entities = {
    colors: colors.filter(color => text.includes(color)),
    garments: garments.filter(garment => text.includes(garment))
  };

  return {
    original: message.trim(),
    intent: intent,
    entities: entities,
    urgency: text.includes('urgent') || text.includes('need now') ? 'high' : 'low'
  };
}

// Build AI prompt
function buildStylistPrompt(analysis) {
  let prompt = `You are Ava, an expert AI fashion stylist for the Colors of Life app. You help users discover their personal style.\n\n`;

  prompt += `Your personality:\n`;
  prompt += `- Warm, encouraging, and enthusiastic about fashion\n`;
  prompt += `- Knowledgeable about trends and timeless style\n`;
  prompt += `- Body-positive and inclusive\n`;
  prompt += `- Conversational and fun\n\n`;

  prompt += `Current message analysis:\n`;
  prompt += `Intent: ${analysis.intent}\n`;
  prompt += `Urgency: ${analysis.urgency}\n`;

  if (Object.values(analysis.entities).some(arr => arr.length > 0)) {
    prompt += `Detected fashion elements:\n`;
    Object.entries(analysis.entities).forEach(([key, values]) => {
      if (values.length > 0) {
        prompt += `- ${key}: ${values.join(', ')}\n`;
      }
    });
  }

  prompt += `\nInstructions:\n`;
  prompt += `1. Respond with personalized fashion advice\n`;
  prompt += `2. Be specific and actionable\n`;
  prompt += `3. Include styling tips when relevant\n`;
  prompt += `4. Ask follow-up questions to understand their needs\n`;
  prompt += `5. Keep responses conversational and encouraging\n`;
  prompt += `6. Limit to 2-3 paragraphs for mobile readability\n\n`;

  prompt += `User's message: "${analysis.original}"\n\n`;
  prompt += `Your response as Ava:`;

  return prompt;
}

const messageAnalysis = analyzeMessage(userMessage);
const aiPrompt = buildStylistPrompt(messageAnalysis);

const result = {
  userId: userId,
  sessionId: sessionId,
  message: {
    original: userMessage,
    analysis: messageAnalysis,
    timestamp: new Date().toISOString()
  },
  aiPrompt: aiPrompt,
  conversationData: {
    intent: messageAnalysis.intent,
    entities: messageAnalysis.entities,
    urgency: messageAnalysis.urgency
  },
  metadata: {
    messageId: `msg_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
    timestamp: new Date().toISOString()
  }
};

console.log('ðŸŽ¯ Message processed:', messageAnalysis.intent);
return result;

// ðŸŽ¨ AI Stylist Response Builder
// Combines AI response with relevant items

const allInputs = $input.all();
let messageData = null;
let userProfile = null;
let aiResponse = null;
let relevantItems = [];

// Process inputs
allInputs.forEach((input) => {
  const data = input.json;

  if (data && data.conversationData && data.aiPrompt) {
    messageData = data;
  } else if (data && data.style_preferences) {
    userProfile = data;
  } else if (data && data.message && data.message.content) {
    aiResponse = data.message.content;
  } else if (Array.isArray(data)) {
    relevantItems = data.map((item, idx) => ({
      id: item.id || `item_${idx}`,
      name: item.name || 'Fashion Item',
      price: item.price ? `$${item.price}` : null,
      brand: item.brand_name || 'Brand',
      colors: item.colors || [],
      source: 'database'
    }));
  }
});

// Fallbacks
if (!messageData) {
  messageData = {
    userId: 'anonymous',
    sessionId: `fallback_${Date.now()}`,
    message: { original: 'fashion advice' },
    conversationData: { intent: 'style_advice', entities: {} }
  };
}

if (!aiResponse) {
  aiResponse = "Hi! I'm Ava, your personal fashion stylist. I'd love to help you discover your style! What are you looking for today?";
}

// Generate follow-up suggestions
const followUps = [];
switch (messageData.conversationData.intent) {
  case 'style_advice':
    followUps.push("Would you like me to find specific items for this look?");
    followUps.push("Tell me about the occasion you're dressing for?");
    break;
  case 'product_search':
    followUps.push("Should I show you more options in different colors?");
    break;
  default:
    followUps.push("Is there anything specific you'd like to explore?");
}

return {
  success: true,
  sessionId: messageData.sessionId,
  conversation: {
    userMessage: messageData.message.original,
    avaResponse: aiResponse,
    intent: messageData.conversationData.intent,
    entities: messageData.conversationData.entities,
    timestamp: new Date().toISOString()
  },
  styling: {
    recommendations: relevantItems,
    followUpSuggestions: followUps.slice(0, 2),
    userProfile: userProfile ? {
      style_preferences: userProfile.style_preferences || [],
      color_preferences: userProfile.color_preferences || [],
      body_type: userProfile.body_type
    } : null
  },
  metadata: {
    responseId: `response_${Date.now()}`,
    userId: messageData.userId,
    timestamp: new Date().toISOString(),
    itemsRecommended: relevantItems.length,
    hasUserProfile: !!userProfile
  }
};
