// ğŸ§  STICKY NOTE: Input Validation & Context Analysis
// Purpose: Validate input and extract user intent for AI processing
// Analyzes: user requests, preferences, context, and required actions

const inputData = $input.first().json;

const {
  userId,
  userMessage,
  requestType, // 'search', 'tryon', 'avatar', 'recommend', 'analyze'
  userPreferences,
  contextData,
  sessionId
} = inputData;

// Validate required fields
if (!userId || !userMessage) {
  throw new Error('Missing required fields: userId, userMessage');
}

// Determine request type if not specified
let detectedType = requestType;
if (!detectedType) {
  const message = userMessage.toLowerCase();
  if (message.includes('search') || message.includes('find')) {
    detectedType = 'search';
  } else if (message.includes('try on') || message.includes('tryon')) {
    detectedType = 'tryon';
  } else if (message.includes('avatar') || message.includes('photo')) {
    detectedType = 'avatar';
  } else if (message.includes('recommend') || message.includes('suggest')) {
    detectedType = 'recommend';
  } else {
    detectedType = 'analyze';
  }
}

return {
  userId,
  userMessage,
  requestType: detectedType,
  userPreferences: userPreferences || {},
  contextData: contextData || {},
  sessionId: sessionId || Date.now().toString(),
  timestamp: new Date().toISOString(),
  processingStarted: true
};

// ğŸ§  STICKY NOTE: Thinking Node - Decision Engine
// Purpose: Process AI agent output and route to appropriate workflows
// Capabilities: Multi-path routing, parallel processing, workflow coordination

const agentResponse = $input.first().json;
const inputData = $('Input Validator & Context Analyzer').first().json;

console.log('AI Agent Response:', JSON.stringify(agentResponse, null, 2));

// Extract action plan from AI agent
const actionPlan = agentResponse.text || agentResponse.output || agentResponse.response;

// Determine which workflows to trigger based on AI decision
const workflows = {
  fashionSearch: actionPlan.includes('FASHION_SEARCH'),
  styleAnalysis: actionPlan.includes('STYLE_ANALYSIS'),
  avatarCreation: actionPlan.includes('AVATAR_CREATION'),
  virtualTryon: actionPlan.includes('VIRTUAL_TRYON'),
  imageEnhancement: actionPlan.includes('IMAGE_ENHANCEMENT')
};

// Prepare routing data
const routingData = {
  ...inputData,
  aiDecision: actionPlan,
  workflows,
  priority: {
    urgent: workflows.avatarCreation || workflows.virtualTryon,
    normal: workflows.fashionSearch || workflows.styleAnalysis,
    background: workflows.imageEnhancement
  },
  executionPlan: {
    parallel: ['fashionSearch', 'styleAnalysis'],
    sequential: ['avatarCreation', 'virtualTryon'],
    async: ['imageEnhancement']
  }
};

return routingData;

// ğŸ§  STICKY NOTE: Response Aggregator & Formatter
// Purpose: Combine all workflow results into comprehensive response
// Features: Error handling, result prioritization, response optimization

const inputData = $('Thinking Node - Decision Engine').first().json;

// Collect results from all executed workflows
const results = {
  fashionSearch: null,
  styleAnalysis: null,
  avatarCreation: null,
  virtualTryon: null,
  timestamp: new Date().toISOString()
};

// Get fashion search results
try {
  const searchResults = $('MCP - Fashion Search Engine').first()?.json;
  if (searchResults) {
    results.fashionSearch = {
      success: true,
      data: searchResults,
      count: searchResults.results?.length || 0
    };
  }
} catch (error) {
  results.fashionSearch = { success: false, error: error.message };
}

// Get style analysis results
try {
  const styleResults = $('MCP - Style Analysis Engine').first()?.json;
  if (styleResults) {
    results.styleAnalysis = {
      success: true,
      data: styleResults,
      recommendations: styleResults.recommendations || []
    };
  }
} catch (error) {
  results.styleAnalysis = { success: false, error: error.message };
}

// Get avatar creation results
try {
  const avatarResults = $('Avatar Creation Sub-workflow').first()?.json;
  if (avatarResults) {
    results.avatarCreation = {
      success: avatarResults.success || false,
      data: avatarResults,
      avatarUrl: avatarResults.avatarUrl
    };
  }
} catch (error) {
  results.avatarCreation = { success: false, error: error.message };
}

// Get virtual try-on results
try {
  const tryonResults = $('Virtual Try-On Sub-workflow').first()?.json;
  if (tryonResults) {
    results.virtualTryon = {
      success: tryonResults.success || false,
      data: tryonResults,
      resultUrl: tryonResults.resultVideoUrl || tryonResults.resultImageUrl
    };
  }
} catch (error) {
  results.virtualTryon = { success: false, error: error.message };
}

// Format comprehensive response
const response = {
  success: true,
  message: "AI Stylist processing completed",
  sessionId: inputData.sessionId,
  userId: inputData.userId,
  originalRequest: inputData.userMessage,
  aiDecision: inputData.aiDecision,
  results,
  metadata: {
    processingTime: Date.now() - new Date(inputData.timestamp).getTime(),
    workflowsExecuted: Object.keys(inputData.workflows).filter(k => inputData.workflows[k]),
    timestamp: new Date().toISOString()
  }
};

return response;
