// ğŸ§  Advanced Query Processor with Color & Image Support
// Enhanced processor for text, color, and image search queries

console.log('=== ADVANCED QUERY PROCESSOR START ===');

// Safely extract input data
let inputData = {};
let searchQuery = '';
let userId = 'anonymous';
let searchType = 'text';
let imageUri = null;
let filters = {};
let maxResults = 20;
let offset = 0;

try {
  const inputs = $input.all();
  if (inputs.length > 0 && inputs[0] && inputs[0].json) {
    inputData = inputs[0].json;
    console.log('âœ… Input received:', Object.keys(inputData));

    // Extract search parameters
    searchQuery = inputData.query || inputData.searchQuery || inputData.q || '';
    userId = inputData.userId || inputData.user_id || 'anonymous';
    searchType = inputData.searchType || (inputData.imageUri ? 'image' : 'text');
    imageUri = inputData.imageUri || null;
    filters = inputData.filters || {};
    maxResults = Math.max(1, Math.min(50, parseInt(inputData.limit || inputData.maxResults) || 20));
    offset = Math.max(0, parseInt(inputData.offset) || 0);

    console.log('ğŸ” Search params:', {
      query: searchQuery,
      type: searchType,
      hasImage: !!imageUri,
      userId,
      filters,
      maxResults,
      offset
    });
  }
} catch (error) {
  console.log('âŒ Error processing input:', error.message);
}

// Enhanced query analysis with color and style detection
function analyzeQuery(query) {
  const original = String(query).toLowerCase().trim();

  // Color detection (expanded list)
  const colorMap = {
    'black': ['black', 'noir', 'dark', 'charcoal', 'ebony'],
    'white': ['white', 'cream', 'ivory', 'off-white', 'pearl'],
    'red': ['red', 'crimson', 'burgundy', 'maroon', 'cherry', 'rose'],
    'blue': ['blue', 'navy', 'royal', 'cobalt', 'azure', 'indigo'],
    'green': ['green', 'emerald', 'forest', 'olive', 'mint', 'sage'],
    'yellow': ['yellow', 'gold', 'mustard', 'amber', 'lemon'],
    'purple': ['purple', 'violet', 'lavender', 'plum', 'orchid'],
    'pink': ['pink', 'rose', 'blush', 'fuchsia', 'magenta', 'coral'],
    'orange': ['orange', 'coral', 'peach', 'tangerine', 'apricot'],
    'brown': ['brown', 'tan', 'beige', 'camel', 'coffee', 'chocolate'],
    'gray': ['gray', 'grey', 'silver', 'charcoal', 'slate'],
    'neutral': ['neutral', 'nude', 'natural', 'beige', 'taupe']
  };

  let detectedColors = [];
  for (const [color, variations] of Object.entries(colorMap)) {
    if (variations.some(variation => original.includes(variation))) {
      detectedColors.push(color);
    }
  }

  // Category detection (expanded)
  let category = 'general';
  const categoryMap = {
    'dresses': ['dress', 'gown', 'frock', 'sundress', 'maxi', 'midi', 'mini'],
    'tops': ['shirt', 'top', 'blouse', 'tee', 't-shirt', 'tank', 'camisole', 'sweater', 'cardigan'],
    'bottoms': ['pants', 'jeans', 'trouser', 'shorts', 'skirt', 'leggings', 'joggers'],
    'shoes': ['shoe', 'boot', 'sneaker', 'sandal', 'heel', 'flat', 'loafer'],
    'outerwear': ['jacket', 'coat', 'blazer', 'cardigan', 'hoodie', 'vest'],
    'accessories': ['bag', 'purse', 'wallet', 'belt', 'hat', 'scarf', 'jewelry']
  };

  for (const [cat, keywords] of Object.entries(categoryMap)) {
    if (keywords.some(keyword => original.includes(keyword))) {
      category = cat;
      break;
    }
  }

  // Style detection (expanded)
  let style = 'general';
  const styleMap = {
    'casual': ['casual', 'relaxed', 'everyday', 'comfortable'],
    'formal': ['formal', 'business', 'professional', 'office', 'work'],
    'party': ['party', 'evening', 'cocktail', 'night out', 'celebration'],
    'summer': ['summer', 'beach', 'vacation', 'light', 'breathable'],
    'winter': ['winter', 'warm', 'cozy', 'thermal', 'layering'],
    'vintage': ['vintage', 'retro', 'classic', 'timeless', 'traditional'],
    'modern': ['modern', 'contemporary', 'trendy', 'current', 'fashionable'],
    'bohemian': ['boho', 'bohemian', 'free-spirited', 'artsy', 'eclectic']
  };

  for (const [styleType, keywords] of Object.entries(styleMap)) {
    if (keywords.some(keyword => original.includes(keyword))) {
      style = styleType;
      break;
    }
  }

  // Occasion detection
  let occasion = 'general';
  const occasionMap = {
    'work': ['work', 'office', 'business', 'professional', 'meeting'],
    'date': ['date', 'romantic', 'dinner', 'date night'],
    'wedding': ['wedding', 'bridal', 'guest', 'ceremony'],
    'travel': ['travel', 'vacation', 'trip', 'airport', 'comfortable'],
    'gym': ['gym', 'workout', 'exercise', 'fitness', 'athletic', 'sports']
  };

  for (const [occ, keywords] of Object.entries(occasionMap)) {
    if (keywords.some(keyword => original.includes(keyword))) {
      occasion = occ;
      break;
    }
  }

  // Create enhanced search terms
  const enhancedTerms = [original];
  if (detectedColors.length > 0) enhancedTerms.push(...detectedColors);
  if (category !== 'general') enhancedTerms.push(category);
  if (style !== 'general') enhancedTerms.push(style);
  if (occasion !== 'general') enhancedTerms.push(occasion);

  // Add fashion-related terms
  enhancedTerms.push('fashion', 'clothing', 'style', 'outfit');

  return {
    original: query.trim(),
    expanded: [...new Set(enhancedTerms)].join(' '),
    colors: detectedColors,
    category: category,
    style: style,
    occasion: occasion,
    searchTerms: enhancedTerms
  };
}

// Process the query
const queryAnalysis = searchType === 'image' ? {
  original: 'image_search',
  expanded: 'fashion clothing style image search',
  colors: filters.colors || [],
  category: 'general',
  style: 'general',
  occasion: 'general',
  searchTerms: ['fashion', 'clothing', 'style']
} : analyzeQuery(searchQuery);

// Merge detected colors with filter colors
const allColors = [...new Set([...queryAnalysis.colors, ...(filters.colors || [])])];

// Build the final processed data
const result = {
  userId: userId,
  searchType: searchType,
  searchQuery: {
    ...queryAnalysis,
    colors: allColors
  },
  imageUri: imageUri,
  filters: {
    ...filters,
    colors: allColors,
    categories: filters.categories || [queryAnalysis.category].filter(c => c !== 'general'),
    priceRange: filters.priceRange || null,
    source: filters.source || 'all'
  },
  maxResults: maxResults,
  offset: offset,
  timestamp: new Date().toISOString(),
  debug: {
    originalInput: inputData,
    detectedColors: queryAnalysis.colors,
    detectedCategory: queryAnalysis.category,
    detectedStyle: queryAnalysis.style,
    detectedOccasion: queryAnalysis.occasion
  }
};

console.log('ğŸ¯ Final processed data:', JSON.stringify(result, null, 2));
console.log('=== ADVANCED QUERY PROCESSOR END ===');

return result;

// ğŸ–¼ï¸ Image Analysis & Processing
// Process uploaded image for visual search

console.log('=== IMAGE PROCESSOR START ===');

const inputData = $input.first().json;
const { imageUri, userId, filters } = inputData;

console.log('ğŸ“¸ Processing image search:', { imageUri, userId });

// Mock image analysis (in real implementation, this would use AI vision services)
const imageAnalysis = {
  dominantColors: ['red', 'white', 'blue'],
  detectedCategories: ['dress', 'formal wear'],
  style: 'elegant',
  patterns: ['solid', 'minimal'],
  confidence: 0.85
};

// Create search query based on image analysis
const generatedQuery = {
  original: 'visual_search',
  expanded: `${imageAnalysis.detectedCategories.join(' ')} ${imageAnalysis.dominantColors.join(' ')} ${imageAnalysis.style} fashion clothing`,
  colors: imageAnalysis.dominantColors,
  category: imageAnalysis.detectedCategories[0] || 'general',
  style: imageAnalysis.style,
  patterns: imageAnalysis.patterns,
  confidence: imageAnalysis.confidence
};

const result = {
  ...inputData,
  imageAnalysis,
  searchQuery: generatedQuery,
  searchType: 'image',
  processedAt: new Date().toISOString()
};

console.log('âœ… Image analysis complete:', result);
console.log('=== IMAGE PROCESSOR END ===');

return result;

// ğŸ¯ Advanced Results Aggregator
// Combines and ranks results from multiple sources

console.log('=== ADVANCED AGGREGATOR START ===');

const allInputs = $input.all();
console.log('ğŸ“Š Total inputs:', allInputs.length);

// Initialize containers
let queryData = null;
let webResults = [];
let dbResults = [];
let imageData = null;

// Process all inputs
allInputs.forEach((input, index) => {
  const data = input.json;
  console.log(`Input ${index}:`, data ? Object.keys(data) : 'null');

  // Query processor data
  if (data && data.searchQuery && data.userId) {
    queryData = data;
    console.log('âœ… Query data found:', data.searchQuery.original);

    // Check if this is image data
    if (data.imageAnalysis) {
      imageData = data.imageAnalysis;
      console.log('ğŸ“¸ Image analysis data found');
    }
  }

  // Web search results from Brave
  else if (data && data.results && Array.isArray(data.results)) {
    console.log('ğŸŒ Web results:', data.results.length);
    webResults = data.results.map((item, idx) => {
      // Enhanced web result processing
      const title = item.title || 'Fashion Item';
      const description = item.snippet || item.description || '';
      const url = item.url || '';

      // Extract potential price from title or description
      const priceMatch = (title + ' ' + description).match(/\$([0-9]+(?:\.[0-9]{2})?)/i);
      const price = priceMatch ? `$${priceMatch[1]}` : null;

      // Try to extract brand from URL or title
      const brandMatch = url.match(/\/\/(?:www\.)?(\w+)\./i);
      const brand = brandMatch ? brandMatch[1].charAt(0).toUpperCase() + brandMatch[1].slice(1) : null;

      return {
        id: `web_${idx}`,
        title: title,
        description: description,
        price: price,
        brand: brand,
        image: null, // Web results typically don't have direct image URLs
        url: url,
        source: 'web',
        type: 'web_result',
        relevanceScore: calculateWebRelevance(item, queryData?.searchQuery),
        isVideo: false
      };
    });
  }

  // Database results
  else if (Array.isArray(data)) {
    console.log('ğŸ—„ï¸ Database results:', data.length);
    dbResults = data.map((item, idx) => {
      const images = parseImages(item.images);
      const discount = item.discount_percentage || 
        (item.original_price && item.price ? 
          Math.round(((item.original_price - item.price) / item.original_price) * 100) : 0);

      return {
        id: item.id || `db_${idx}`,
        title: item.name || 'Fashion Item',
        description: item.description || '',
        image: images,
        price: item.price ? `$${item.price}` : null,
        originalPrice: item.original_price ? `$${item.original_price}` : null,
        brand: item.brand_name || item.brand_id,
        category: item.category_name || item.category_id,
        colors: item.colors || [],
        sizes: item.sizes || [],
        tags: item.tags || [],
        materials: item.materials || [],
        productCode: item.product_code,
        styleAttributes: item.style_attributes || {},
        source: 'database',
        type: 'fashion_item',
        relevanceScore: calculateDbRelevance(item, queryData?.searchQuery),
        discount: discount,
        isVideo: false,
        popularityScore: item.popularity_score || 0
      };
    });
  }
});

// Fallback query data
if (!queryData) {
  console.log('âš ï¸ Using fallback query data');
  queryData = {
    searchQuery: { original: 'fashion', expanded: 'fashion clothing', category: 'general', colors: [] },
    userId: 'anonymous',
    maxResults: 20,
    searchType: 'text'
  };
}

// Helper functions
function parseImages(images) {
  if (!images) return null;
  if (typeof images === 'string') {
    try {
      const parsed = JSON.parse(images);
      return Array.isArray(parsed) ? parsed[0] : parsed;
    } catch { return images; }
  }
  return Array.isArray(images) ? images[0] : images;
}

function calculateWebRelevance(item, searchQuery) {
  if (!searchQuery) return 1;

  const query = searchQuery.original.toLowerCase();
  const title = (item.title || '').toLowerCase();
  const description = (item.snippet || item.description || '').toLowerCase();

  let score = 0;

  // Exact matches
  if (title.includes(query)) score += 40;
  if (description.includes(query)) score += 20;

  // Color matching
  if (searchQuery.colors && searchQuery.colors.length > 0) {
    searchQuery.colors.forEach(color => {
      if (title.includes(color) || description.includes(color)) {
        score += 25;
      }
    });
  }

  // Category matching
  if (searchQuery.category && searchQuery.category !== 'general') {
    if (title.includes(searchQuery.category) || description.includes(searchQuery.category)) {
      score += 20;
    }
  }

  // Word matching
  const queryWords = query.split(' ').filter(w => w.length > 2);
  queryWords.forEach(word => {
    if (title.includes(word)) score += 10;
    if (description.includes(word)) score += 5;
  });

  // Fashion-specific bonus
  const fashionTerms = ['fashion', 'style', 'clothing', 'apparel', 'outfit', 'wear'];
  fashionTerms.forEach(term => {
    if (title.includes(term) || description.includes(term)) {
      score += 3;
    }
  });

  return Math.max(score, 1);
}

function calculateDbRelevance(item, searchQuery) {
  if (!searchQuery) return 1;

  const query = searchQuery.original.toLowerCase();
  const title = (item.name || '').toLowerCase();
  const description = (item.description || '').toLowerCase();
  const tags = (item.tags || []).join(' ').toLowerCase();

  let score = 0;

  // Exact matches (higher weight for database items)
  if (title.includes(query)) score += 50;
  if (description.includes(query)) score += 30;
  if (tags.includes(query)) score += 20;

  // Color matching (exact match since we have structured data)
  if (searchQuery.colors && searchQuery.colors.length > 0) {
    const itemColors = item.colors || [];
    const colorMatches = searchQuery.colors.filter(color => itemColors.includes(color));
    score += colorMatches.length * 30;
  }

  // Category matching
  if (searchQuery.category && searchQuery.category !== 'general') {
    if (item.category_name && item.category_name.toLowerCase().includes(searchQuery.category)) {
      score += 25;
    }
  }

  // Brand matching
  if (item.brand_name && query.includes(item.brand_name.toLowerCase())) {
    score += 20;
  }

  // Popularity bonus
  score += (item.popularity_score || 0) * 0.1;

  // Database item bonus (we trust our own data more)
  score += 10;

  // Discount bonus (users like deals)
  if (item.discount_percentage > 0) {
    score += 5;
  }

  return Math.max(score, 1);
}

// Combine and rank results
let combinedResults = [...webResults, ...dbResults];

// Apply additional filtering based on search type
if (queryData.searchType === 'image' && imageData) {
  // For image search, prioritize items with similar colors
  combinedResults = combinedResults.map(item => {
    let bonus = 0;
    if (item.colors && imageData.dominantColors) {
      const colorMatches = item.colors.filter(color => 
        imageData.dominantColors.includes(color)
      );
      bonus = colorMatches.length * 20;
    }
    return {
      ...item,
      relevanceScore: item.relevanceScore + bonus
    };
  });
}

// Sort by relevance score
combinedResults.sort((a, b) => b.relevanceScore - a.relevanceScore);

// Limit results
combinedResults = combinedResults.slice(0, queryData.maxResults || 20);

console.log('ğŸ“ˆ Final results:', combinedResults.length);
console.log('ğŸ¯ Top 3 results:', combinedResults.slice(0, 3).map(r => ({ title: r.title, score: r.relevanceScore })));

// Build comprehensive response
const finalResult = {
  success: true,
  query: queryData.searchQuery,
  results: combinedResults,
  metadata: {
    total: combinedResults.length,
    webResults: webResults.length,
    databaseResults: dbResults.length,
    sources: [],
    searchType: queryData.searchType,
    timestamp: new Date().toISOString(),
    filters: queryData.filters || {},
    imageAnalysis: imageData || null
  },
  debug: {
    inputsProcessed: allInputs.length,
    queryFound: !!queryData,
    finalQuery: queryData.searchQuery.original,
    topScores: combinedResults.slice(0, 5).map(r => r.relevanceScore)
  }
};

// Add sources
if (webResults.length > 0) finalResult.metadata.sources.push('web');
if (dbResults.length > 0) finalResult.metadata.sources.push('database');

console.log('ğŸ¯ SUCCESS - Query:', finalResult.query.original);
console.log('ğŸ¯ SUCCESS - Results:', finalResult.results.length);
console.log('ğŸ¯ SUCCESS - Sources:', finalResult.metadata.sources);
console.log('=== ADVANCED AGGREGATOR END ===');

return finalResult;

// ğŸš¨ Enhanced No Results Handler
// Provides intelligent suggestions and fallback options

const originalData = $input.first().json;
const query = originalData.query?.original || 'your search';
const searchType = originalData.metadata?.searchType || 'text';
const filters = originalData.metadata?.filters || {};

// Generate intelligent suggestions based on search context
const generateSuggestions = (query, searchType, filters) => {
  const suggestions = {
    tryBroaderTerms: [],
    popularSearches: [],
    searchTips: [],
    colorSuggestions: [],
    categorySuggestions: []
  };

  if (searchType === 'image') {
    suggestions.tryBroaderTerms = [
      'Try taking a clearer photo',
      'Focus on a single clothing item',
      'Ensure good lighting in your image'
    ];
    suggestions.searchTips = [
      'Use images with clear, well-lit clothing items',
      'Avoid cluttered backgrounds',
      'Try different angles or closer shots'
    ];
  } else {
    suggestions.tryBroaderTerms = [
      'Try simpler terms like "dress" or "shirt"',
      'Remove specific colors or styles',
      'Check spelling of fashion terms',
      'Use more general categories'
    ];
    suggestions.searchTips = [
      'Use basic terms like "dress", "shirt", "pants"',
      'Try different color names',
      'Search by category: tops, bottoms, dresses',
      'Include occasion: work, casual, formal'
    ];
  }

  // Popular searches based on season/trends
  const currentMonth = new Date().getMonth();
  const isWinter = currentMonth >= 11 || currentMonth <= 1;
  const isSummer = currentMonth >= 5 && currentMonth <= 7;

  if (isWinter) {
    suggestions.popularSearches = [
      'winter coat', 'warm sweater', 'boots', 'cozy cardigan',
      'formal wear', 'holiday dress', 'layering pieces'
    ];
  } else if (isSummer) {
    suggestions.popularSearches = [
      'summer dress', 'sandals', 'shorts', 'tank top',
      'swimwear', 'light cardigan', 'sun hat'
    ];
  } else {
    suggestions.popularSearches = [
      'casual shirt', 'jeans', 'sneakers', 'spring dress',
      'light jacket', 'comfortable shoes', 'versatile top'
    ];
  }

  // Color suggestions
  suggestions.colorSuggestions = [
    'black', 'white', 'blue', 'red', 'green',
    'neutral', 'pink', 'gray', 'brown'
  ];

  // Category suggestions
  suggestions.categorySuggestions = [
    'dresses', 'tops', 'bottoms', 'shoes',
    'outerwear', 'accessories', 'activewear'
  ];

  return suggestions;
};

const suggestions = generateSuggestions(query, searchType, filters);

// Create contextual message
let message = `No items found for "${query}"`;
if (searchType === 'image') {
  message = 'No similar items found in your image search';
} else if (filters.colors && filters.colors.length > 0) {
  message += ` with colors: ${filters.colors.join(', ')}`;
}

const response = {
  success: false,
  query: originalData.query,
  results: [],
  message: message,
  suggestions: suggestions,
  metadata: {
    total: 0,
    webResults: 0,
    databaseResults: 0,
    sources: [],
    searchType: searchType,
    timestamp: new Date().toISOString(),
    filters: filters
  },
  debug: {
    originalQuery: query,
    searchType: searchType,
    appliedFilters: filters
  }
};

console.log('ğŸš¨ No results for:', query);
console.log('ğŸ’¡ Suggestions generated:', suggestions.tryBroaderTerms.length);

return response;

// ğŸ“Š Comprehensive Analytics Logger
// Track search patterns, performance, and user behavior

const data = $input.first().json;
const timestamp = new Date().toISOString();

// Extract analytics data
const analytics = {
  searchId: `search_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
  timestamp: timestamp,
  userId: data.debug?.userId || 'anonymous',
  searchType: data.metadata?.searchType || 'text',
  query: data.query?.original || 'unknown',
  expandedQuery: data.query?.expanded || '',
  category: data.query?.category || 'general',
  colors: data.query?.colors || [],
  style: data.query?.style || 'general',
  occasion: data.query?.occasion || 'general',
  resultsCount: data.results?.length || 0,
  sources: data.metadata?.sources || [],
  success: data.success || false,
  filters: data.metadata?.filters || {},
  performance: {
    totalSources: data.metadata?.sources?.length || 0,
    webResults: data.metadata?.webResults || 0,
    databaseResults: data.metadata?.databaseResults || 0,
    topRelevanceScore: data.debug?.topScores?.[0] || 0
  },
  imageAnalysis: data.metadata?.imageAnalysis || null
};

// Log different types of search patterns
if (analytics.searchType === 'image') {
  console.log('ğŸ“¸ Image search analytics:', {
    hasImageAnalysis: !!analytics.imageAnalysis,
    dominantColors: analytics.imageAnalysis?.dominantColors || [],
    detectedCategories: analytics.imageAnalysis?.detectedCategories || [],
    confidence: analytics.imageAnalysis?.confidence || 0
  });
}

if (analytics.colors.length > 0) {
  console.log('ğŸ¨ Color search analytics:', {
    colors: analytics.colors,
    query: analytics.query,
    results: analytics.resultsCount
  });
}

if (analytics.resultsCount === 0) {
  console.log('âš ï¸ Zero results analytics:', {
    query: analytics.query,
    expandedQuery: analytics.expandedQuery,
    filters: analytics.filters,
    searchType: analytics.searchType
  });
}

if (analytics.resultsCount > 15) {
  console.log('ğŸ‰ High results analytics:', {
    query: analytics.query,
    resultsCount: analytics.resultsCount,
    sources: analytics.sources
  });
}

// Track search performance
const performanceMetrics = {
  searchLatency: Date.now() - new Date(analytics.timestamp).getTime(),
  sourcesUsed: analytics.sources.length,
  webToDbRatio: analytics.performance.webResults / Math.max(analytics.performance.databaseResults, 1),
  averageRelevanceScore: analytics.performance.topRelevanceScore
};

console.log('âš¡ Performance metrics:', performanceMetrics);

// User behavior insights
if (analytics.userId !== 'anonymous') {
  console.log('ğŸ‘¤ User behavior:', {
    userId: analytics.userId,
    searchType: analytics.searchType,
    preferredCategories: analytics.category,
    colorPreferences: analytics.colors
  });
}

// Trend analysis
const trendData = {
  popularQuery: analytics.query,
  searchVolume: 1, // Would aggregate in real analytics
  categoryTrend: analytics.category,
  colorTrend: analytics.colors,
  seasonalContext: {
    month: new Date().getMonth(),
    season: new Date().getMonth() >= 3 && new Date().getMonth() <= 5 ? 'spring' :
             new Date().getMonth() >= 6 && new Date().getMonth() <= 8 ? 'summer' :
             new Date().getMonth() >= 9 && new Date().getMonth() <= 11 ? 'fall' : 'winter'
  }
};

console.log('ğŸ“ˆ Trend analysis:', trendData);

// Quality metrics
const qualityMetrics = {
  searchSuccess: analytics.success,
  queryComplexity: analytics.query.split(' ').length,
  filterUsage: Object.keys(analytics.filters).length,
  resultDiversity: analytics.sources.length,
  userEngagement: analytics.resultsCount > 0 ? 'high' : 'low'
};

console.log('ğŸ“Š Quality metrics:', qualityMetrics);

// Store analytics (in production, this would go to analytics service)
console.log('ğŸ“Š COMPREHENSIVE ANALYTICS:', JSON.stringify({
  ...analytics,
  performance: performanceMetrics,
  trends: trendData,
  quality: qualityMetrics
}, null, 2));

// Return original data (pass-through)
return data;
