// ğŸ§  Smart Fashion Query Processor
// Analyzes and enhances user search queries with robust error handling

console.log('=== QUERY PROCESSOR START ===');

// Safely get input data
let inputData = {};
let searchQuery = 'fashion';
let userId = 'anonymous';
let maxResults = 20;

try {
  const inputs = $input.all();
  if (inputs.length > 0 && inputs[0] && inputs[0].json) {
    inputData = inputs[0].json;
    console.log('âœ… Input received:', Object.keys(inputData));

    // Extract query from multiple possible fields
    searchQuery = inputData.query || inputData.searchQuery || inputData.q || inputData.search || 'fashion';
    userId = inputData.user_id || inputData.userId || inputData.user || 'anonymous';
    maxResults = Math.max(1, Math.min(100, parseInt(inputData.maxResults || inputData.limit) || 20));

    console.log('ğŸ” Extracted query:', searchQuery);
    console.log('ğŸ‘¤ User ID:', userId);
  }
} catch (error) {
  console.log('âŒ Error processing input:', error.message);
}

// Enhanced query analysis
function analyzeQuery(query) {
  const original = String(query).toLowerCase().trim();

  // Category detection
  let category = 'general';
  if (original.includes('dress')) category = 'dresses';
  else if (original.includes('shirt') || original.includes('top') || original.includes('blouse')) category = 'tops';
  else if (original.includes('pants') || original.includes('jeans') || original.includes('trouser')) category = 'bottoms';
  else if (original.includes('shoe') || original.includes('boot') || original.includes('sneaker')) category = 'shoes';

  // Color detection
  const colors = [];
  const colorList = ['red', 'blue', 'green', 'yellow', 'black', 'white', 'pink', 'purple', 'orange', 'brown', 'gray', 'grey', 'navy', 'beige'];
  colorList.forEach(color => {
    if (original.includes(color)) colors.push(color);
  });

  // Style detection
  let style = 'general';
  if (original.includes('casual')) style = 'casual';
  else if (original.includes('formal') || original.includes('office')) style = 'formal';
  else if (original.includes('summer') || original.includes('light')) style = 'summer';
  else if (original.includes('winter') || original.includes('warm')) style = 'winter';

  // Create expanded query
  const expandedTerms = [original];
  if (colors.length > 0) expandedTerms.push(...colors);
  if (category !== 'general') expandedTerms.push(category);
  expandedTerms.push('fashion', 'clothing');

  return {
    original: query.trim(),
    expanded: [...new Set(expandedTerms)].join(' '),
    category: category,
    style: style,
    colors: colors
  };
}

const queryAnalysis = analyzeQuery(searchQuery);

const result = {
  userId: userId,
  searchQuery: queryAnalysis,
  maxResults: maxResults,
  searchType: 'comprehensive',
  timestamp: new Date().toISOString(),
  debug: {
    originalInput: inputData,
    processedQuery: queryAnalysis.original
  }
};

console.log('ğŸ¯ Final processed data:', JSON.stringify(result, null, 2));
console.log('=== QUERY PROCESSOR END ===');

return result;

// ğŸ¯ FINAL Smart Results Aggregator
// Combines web and database results with intelligent ranking

console.log('=== FINAL AGGREGATOR START ===');

const allInputs = $input.all();
console.log('ğŸ“Š Total inputs:', allInputs.length);

// Initialize containers
let queryData = null;
let webResults = [];
let dbResults = [];

// Process inputs
allInputs.forEach((input, index) => {
  const data = input.json;
  console.log(`Input ${index}:`, data ? Object.keys(data) : 'null');

  // Query processor data
  if (data && data.searchQuery && data.userId) {
    queryData = data;
    console.log('âœ… Query data found:', data.searchQuery.original);
  }

  // Web search results
  else if (data && data.results && Array.isArray(data.results)) {
    console.log('ğŸŒ Web results:', data.results.length);
    webResults = data.results.map((item, idx) => ({
      id: `web_${idx}`,
      title: item.title || 'Web Result',
      description: item.snippet || item.description || '',
      url: item.url,
      source: 'web',
      type: 'web_result',
      relevanceScore: calculateRelevance(item, queryData?.searchQuery)
    }));
  }

  // Database results
  else if (Array.isArray(data)) {
    console.log('ğŸ—„ï¸ Database results:', data.length);
    dbResults = data.map((item, idx) => ({
      id: item.id || `db_${idx}`,
      title: item.name || 'Fashion Item',
      description: item.description || '',
      image: parseImages(item.images),
      price: item.price ? `$${item.price}` : null,
      originalPrice: item.original_price ? `$${item.original_price}` : null,
      brand: item.brand_name || item.brand_id,
      category: item.category_name || item.category_id,
      colors: item.colors || [],
      sizes: item.sizes || [],
      tags: item.tags || [],
      materials: item.materials || [],
      productCode: item.product_code,
      source: 'internal',
      type: 'fashion_item',
      relevanceScore: calculateRelevance(item, queryData?.searchQuery)
    }));
  }
});

// Fallback query data
if (!queryData) {
  console.log('âš ï¸ Using fallback query data');
  queryData = {
    searchQuery: { original: 'fashion', expanded: 'fashion clothing', category: 'general', colors: [] },
    userId: 'anonymous',
    maxResults: 20
  };
}

// Helper functions
function parseImages(images) {
  if (!images) return null;
  if (typeof images === 'string') {
    try {
      const parsed = JSON.parse(images);
      return Array.isArray(parsed) ? parsed[0] : parsed;
    } catch { return images; }
  }
  return Array.isArray(images) ? images[0] : images;
}

function calculateRelevance(item, searchQuery) {
  if (!searchQuery) return 1;

  const query = searchQuery.original.toLowerCase();
  const title = (item.title || item.name || '').toLowerCase();
  const description = (item.description || item.snippet || '').toLowerCase();

  let score = 0;

  // Exact matches
  if (title.includes(query)) score += 30;
  if (description.includes(query)) score += 15;

  // Color matching
  if (searchQuery.colors && searchQuery.colors.length > 0) {
    searchQuery.colors.forEach(color => {
      if (title.includes(color) || description.includes(color)) {
        score += 20;
      }
    });
  }

  // Word matching
  const queryWords = query.split(' ').filter(w => w.length > 2);
  queryWords.forEach(word => {
    if (title.includes(word)) score += 8;
    if (description.includes(word)) score += 5;
  });

  // Quality bonuses
  if (item.source === 'internal') score += 5;
  if (item.image || item.images) score += 3;
  if (item.price) score += 2;

  return Math.max(score, 1);
}

// Combine and sort results
const combinedResults = [...webResults, ...dbResults]
  .sort((a, b) => b.relevanceScore - a.relevanceScore)
  .slice(0, queryData.maxResults || 20);

console.log('ğŸ“ˆ Final results:', combinedResults.length);

// Build response
const finalResult = {
  success: true,
  query: queryData.searchQuery,
  results: combinedResults,
  metadata: {
    total: combinedResults.length,
    webResults: webResults.length,
    internalResults: dbResults.length,
    sources: [],
    timestamp: new Date().toISOString()
  },
  debug: {
    inputsProcessed: allInputs.length,
    queryFound: !!queryData,
    finalQuery: queryData.searchQuery.original
  }
};

// Add sources
if (webResults.length > 0) finalResult.metadata.sources.push('web');
if (dbResults.length > 0) finalResult.metadata.sources.push('internal');

console.log('ğŸ¯ SUCCESS - Query:', finalResult.query.original);
console.log('ğŸ¯ SUCCESS - Results:', finalResult.results.length);
console.log('=== FINAL AGGREGATOR END ===');

return finalResult;

// ğŸš¨ No Results Handler with Smart Suggestions

const originalData = $input.first().json;
const query = originalData.query?.original || 'your search';

const suggestions = {
  tryBroaderTerms: [
    'Try simpler terms like "dress" or "shirt"',
    'Remove specific colors or styles',
    'Check spelling of fashion terms'
  ],
  popularSearches: [
    'red dress',
    'casual shirt',
    'blue jeans',
    'summer clothing',
    'formal wear'
  ],
  searchTips: [
    'Use basic terms like "dress", "shirt", "pants"',
    'Try different color names',
    'Search by category: tops, bottoms, dresses'
  ]
};

const response = {
  success: false,
  query: originalData.query,
  results: [],
  message: `No fashion items found for "${query}"`,
  suggestions: suggestions,
  metadata: {
    total: 0,
    webResults: 0,
    internalResults: 0,
    sources: [],
    timestamp: new Date().toISOString()
  }
};

console.log('ğŸš¨ No results for:', query);
return response;

// ğŸ“Š Analytics Logger
// Track search patterns and performance

const data = $input.first().json;

const analytics = {
  searchId: `search_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
  timestamp: new Date().toISOString(),
  query: data.query?.original || 'unknown',
  category: data.query?.category || 'general',
  colors: data.query?.colors || [],
  resultsCount: data.results?.length || 0,
  sources: data.metadata?.sources || [],
  success: data.success || false
};

console.log('ğŸ“Š Analytics:', JSON.stringify(analytics, null, 2));

// Log interesting patterns
if (analytics.colors.length > 0) {
  console.log('ğŸ¨ Color search:', analytics.colors.join(', '));
}

if (analytics.resultsCount === 0) {
  console.log('âš ï¸ Zero results for:', analytics.query);
}

if (analytics.resultsCount > 10) {
  console.log('ğŸ‰ High results for:', analytics.query);
}

// Return original data (pass-through)
return data;
