// ðŸ§  Advanced Query Processor for Fashion Search
// Processes search queries with color, style, and context detection

console.log('=== SMART SEARCH QUERY PROCESSOR START ===');

// Initialize default values
let inputData = {};
let searchQuery = '';
let userId = 'anonymous';
let filters = {};
let maxResults = 20;
let offset = 0;

try {
  // Extract input data from webhook
  const inputs = $input.all();
  if (inputs.length > 0 && inputs[0] && inputs[0].json) {
    inputData = inputs[0].json;
    console.log('âœ… Input received:', Object.keys(inputData));

    // Extract parameters
    searchQuery = inputData.query || inputData.searchQuery || inputData.q || '';
    userId = inputData.userId || inputData.user_id || 'anonymous';
    filters = inputData.filters || {};
    maxResults = Math.max(1, Math.min(50, parseInt(inputData.limit || inputData.maxResults) || 20));
    offset = Math.max(0, parseInt(inputData.offset) || 0);

    console.log('ðŸ” Extracted params:', {
      query: searchQuery,
      userId,
      maxResults,
      filters
    });
  }
} catch (error) {
  console.log('âŒ Error processing input:', error.message);
}

// Advanced query analysis with fashion-specific intelligence
function analyzeQuery(query) {
  const original = String(query).toLowerCase().trim();

  // Enhanced color detection
  const colorMap = {
    'black': ['black', 'noir', 'dark', 'charcoal'],
    'white': ['white', 'cream', 'ivory', 'off-white'],
    'red': ['red', 'crimson', 'burgundy', 'maroon'],
    'blue': ['blue', 'navy', 'royal', 'cobalt'],
    'green': ['green', 'emerald', 'forest', 'olive'],
    'yellow': ['yellow', 'gold', 'mustard', 'amber'],
    'purple': ['purple', 'violet', 'lavender', 'plum'],
    'pink': ['pink', 'rose', 'blush', 'fuchsia'],
    'orange': ['orange', 'coral', 'peach', 'tangerine'],
    'brown': ['brown', 'tan', 'beige', 'camel'],
    'gray': ['gray', 'grey', 'silver', 'charcoal']
  };

  let detectedColors = [];
  for (const [color, variations] of Object.entries(colorMap)) {
    if (variations.some(variation => original.includes(variation))) {
      detectedColors.push(color);
    }
  }

  // Category detection
  let category = 'general';
  const categoryMap = {
    'dresses': ['dress', 'gown', 'frock', 'sundress'],
    'tops': ['shirt', 'top', 'blouse', 'tee', 'sweater'],
    'bottoms': ['pants', 'jeans', 'trouser', 'shorts', 'skirt'],
    'shoes': ['shoe', 'boot', 'sneaker', 'sandal', 'heel'],
    'outerwear': ['jacket', 'coat', 'blazer', 'vest'],
    'accessories': ['bag', 'purse', 'wallet', 'belt', 'hat']
  };

  for (const [cat, keywords] of Object.entries(categoryMap)) {
    if (keywords.some(keyword => original.includes(keyword))) {
      category = cat;
      break;
    }
  }

  return {
    original: query.trim(),
    expanded: [original, ...detectedColors, category !== 'general' ? category : '', 'fashion'].filter(Boolean).join(' '),
    colors: detectedColors,
    category: category,
    intent: 'general_search'
  };
}

// Process the query
const queryAnalysis = analyzeQuery(searchQuery);

// Build result
const result = {
  userId: userId,
  searchQuery: queryAnalysis,
  filters: {
    ...filters,
    colors: [...new Set([...queryAnalysis.colors, ...(filters.colors || [])])],
    categories: filters.categories || [queryAnalysis.category].filter(c => c !== 'general')
  },
  maxResults: maxResults,
  offset: offset,
  timestamp: new Date().toISOString(),
  searchId: `search_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`
};

console.log('ðŸŽ¯ Final processed data:', JSON.stringify(result, null, 2));
return result;

// ðŸŽ¯ Smart Results Aggregator
// Combines database and web results

const allInputs = $input.all();
let queryData = null;
let dbResults = [];
let webResults = [];

// Process inputs
allInputs.forEach((input) => {
  const data = input.json;

  if (data && data.searchQuery && data.userId) {
    queryData = data;
  } else if (Array.isArray(data)) {
    dbResults = data.map((item, idx) => ({
      id: item.id || `db_${idx}`,
      title: item.name || 'Fashion Item',
      description: item.description || '',
      price: item.price ? `$${item.price}` : null,
      brand: item.brand_name || 'Brand',
      category: item.category_name || 'Fashion',
      colors: item.colors || [],
      source: 'database',
      relevanceScore: 50
    }));
  } else if (data && data.webPages) {
    webResults = data.webPages.value?.map((item, idx) => ({
      id: `web_${idx}`,
      title: item.name || 'Fashion Item',
      description: item.snippet || '',
      url: item.url || '',
      source: 'web',
      relevanceScore: 25
    })) || [];
  }
});

// Fallback
if (!queryData) {
  queryData = {
    searchQuery: { original: 'fashion' },
    userId: 'anonymous',
    searchId: `fallback_${Date.now()}`
  };
}

// Combine results
let combinedResults = [...dbResults, ...webResults];
combinedResults.sort((a, b) => b.relevanceScore - a.relevanceScore);
combinedResults = combinedResults.slice(0, 20);

return {
  success: true,
  query: queryData.searchQuery,
  results: combinedResults,
  metadata: {
    total: combinedResults.length,
    dbResults: dbResults.length,
    webResults: webResults.length,
    searchId: queryData.searchId,
    timestamp: new Date().toISOString()
  }
};
