// ðŸ§  Advanced Query Processor for Fashion Search
// Processes search queries with color, style, and context detection

console.log('=== SMART SEARCH QUERY PROCESSOR START ===');

// Initialize default values
let inputData = {};
let searchQuery = '';
let userId = 'anonymous';
let filters = {};
let maxResults = 20;
let offset = 0;

try {
  // Extract input data from webhook
  const inputs = $input.all();
  if (inputs.length > 0 && inputs[0] && inputs[0].json) {
    inputData = inputs[0].json;
    console.log('âœ… Input received:', Object.keys(inputData));

    // Extract parameters
    searchQuery = inputData.query || inputData.searchQuery || inputData.q || '';
    userId = inputData.userId || inputData.user_id || 'anonymous';
    filters = inputData.filters || {};
    maxResults = Math.max(1, Math.min(50, parseInt(inputData.limit || inputData.maxResults) || 20));
    offset = Math.max(0, parseInt(inputData.offset) || 0);

    console.log('ðŸ” Extracted params:', {
      query: searchQuery,
      userId,
      maxResults,
      filters
    });
  }
} catch (error) {
  console.log('âŒ Error processing input:', error.message);
}

// Advanced query analysis with fashion-specific intelligence
function analyzeQuery(query) {
  const original = String(query).toLowerCase().trim();

  // Enhanced color detection
  const colorMap = {
    'black': ['black', 'noir', 'dark', 'charcoal', 'ebony', 'jet'],
    'white': ['white', 'cream', 'ivory', 'off-white', 'pearl', 'snow'],
    'red': ['red', 'crimson', 'burgundy', 'maroon', 'cherry', 'rose', 'scarlet'],
    'blue': ['blue', 'navy', 'royal', 'cobalt', 'azure', 'indigo', 'sapphire'],
    'green': ['green', 'emerald', 'forest', 'olive', 'mint', 'sage', 'lime'],
    'yellow': ['yellow', 'gold', 'mustard', 'amber', 'lemon', 'sunny'],
    'purple': ['purple', 'violet', 'lavender', 'plum', 'orchid', 'amethyst'],
    'pink': ['pink', 'rose', 'blush', 'fuchsia', 'magenta', 'coral'],
    'orange': ['orange', 'coral', 'peach', 'tangerine', 'apricot', 'rust'],
    'brown': ['brown', 'tan', 'beige', 'camel', 'coffee', 'chocolate', 'khaki'],
    'gray': ['gray', 'grey', 'silver', 'charcoal', 'slate', 'ash'],
    'neutral': ['neutral', 'nude', 'natural', 'beige', 'taupe', 'sand']
  };

  let detectedColors = [];
  for (const [color, variations] of Object.entries(colorMap)) {
    if (variations.some(variation => original.includes(variation))) {
      detectedColors.push(color);
    }
  }

  // Category detection with fashion-specific terms
  let category = 'general';
  const categoryMap = {
    'dresses': ['dress', 'gown', 'frock', 'sundress', 'maxi', 'midi', 'mini', 'cocktail'],
    'tops': ['shirt', 'top', 'blouse', 'tee', 't-shirt', 'tank', 'camisole', 'sweater', 'cardigan', 'hoodie'],
    'bottoms': ['pants', 'jeans', 'trouser', 'shorts', 'skirt', 'leggings', 'joggers', 'chinos'],
    'shoes': ['shoe', 'boot', 'sneaker', 'sandal', 'heel', 'flat', 'loafer', 'pump', 'oxford'],
    'outerwear': ['jacket', 'coat', 'blazer', 'vest', 'parka', 'trench', 'bomber'],
    'accessories': ['bag', 'purse', 'wallet', 'belt', 'hat', 'scarf', 'jewelry', 'watch', 'sunglasses']
  };

  for (const [cat, keywords] of Object.entries(categoryMap)) {
    if (keywords.some(keyword => original.includes(keyword))) {
      category = cat;
      break;
    }
  }

  // Style and occasion detection
  let style = 'general';
  const styleMap = {
    'casual': ['casual', 'relaxed', 'everyday', 'comfortable', 'laid-back'],
    'formal': ['formal', 'business', 'professional', 'office', 'work', 'corporate'],
    'party': ['party', 'evening', 'cocktail', 'night out', 'celebration', 'festive'],
    'summer': ['summer', 'beach', 'vacation', 'light', 'breathable', 'sunny'],
    'winter': ['winter', 'warm', 'cozy', 'thermal', 'layering', 'cold'],
    'vintage': ['vintage', 'retro', 'classic', 'timeless', 'traditional', 'old-school'],
    'modern': ['modern', 'contemporary', 'trendy', 'current', 'fashionable', 'latest'],
    'bohemian': ['boho', 'bohemian', 'free-spirited', 'artsy', 'eclectic', 'hippie'],
    'athletic': ['athletic', 'sporty', 'gym', 'workout', 'fitness', 'active']
  };

  for (const [styleType, keywords] of Object.entries(styleMap)) {
    if (keywords.some(keyword => original.includes(keyword))) {
      style = styleType;
      break;
    }
  }

  // Price range detection
  let priceRange = null;
  if (original.includes('cheap') || original.includes('budget') || original.includes('affordable')) {
    priceRange = { min: 0, max: 50 };
  } else if (original.includes('expensive') || original.includes('luxury') || original.includes('premium')) {
    priceRange = { min: 200, max: 1000 };
  } else if (original.includes('mid-range') || original.includes('moderate')) {
    priceRange = { min: 50, max: 200 };
  }

  // Create enhanced search terms
  const enhancedTerms = [original];
  if (detectedColors.length > 0) enhancedTerms.push(...detectedColors);
  if (category !== 'general') enhancedTerms.push(category);
  if (style !== 'general') enhancedTerms.push(style);

  // Add general fashion terms
  enhancedTerms.push('fashion', 'clothing', 'style', 'outfit');

  return {
    original: query.trim(),
    expanded: [...new Set(enhancedTerms)].join(' '),
    colors: detectedColors,
    category: category,
    style: style,
    priceRange: priceRange,
    searchTerms: enhancedTerms,
    intent: determineSearchIntent(original)
  };
}

// Determine user search intent
function determineSearchIntent(query) {
  if (query.includes('similar') || query.includes('like this')) return 'similar';
  if (query.includes('outfit') || query.includes('look')) return 'complete_outfit';
  if (query.includes('match') || query.includes('goes with')) return 'coordination';
  if (query.includes('trend') || query.includes('popular')) return 'trending';
  if (query.includes('occasion') || query.includes('event')) return 'occasion_based';
  return 'general_search';
}

// Process the query
const queryAnalysis = analyzeQuery(searchQuery);

// Merge with provided filters
const allColors = [...new Set([...queryAnalysis.colors, ...(filters.colors || [])])];
const finalPriceRange = filters.priceRange || queryAnalysis.priceRange;

// Build comprehensive search data
const result = {
  userId: userId,
  searchQuery: {
    ...queryAnalysis,
    colors: allColors
  },
  filters: {
    ...filters,
    colors: allColors,
    categories: filters.categories || [queryAnalysis.category].filter(c => c !== 'general'),
    priceRange: finalPriceRange,
    source: filters.source || 'all'
  },
  maxResults: maxResults,
  offset: offset,
  timestamp: new Date().toISOString(),
  searchId: `search_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
  debug: {
    originalInput: inputData,
    detectedColors: queryAnalysis.colors,
    detectedCategory: queryAnalysis.category,
    detectedStyle: queryAnalysis.style,
    searchIntent: queryAnalysis.intent,
    enhancedQuery: queryAnalysis.expanded
  }
};

console.log('ðŸŽ¯ Final processed data:', JSON.stringify(result, null, 2));
console.log('=== SMART SEARCH QUERY PROCESSOR END ===');

return result;

// ðŸŽ¯ Smart Results Aggregator and Ranking Engine
// Combines database and web results with intelligent ranking

console.log('=== SMART RESULTS AGGREGATOR START ===');

const allInputs = $input.all();
console.log('ðŸ“Š Total inputs:', allInputs.length);

// Initialize containers
let queryData = null;
let dbResults = [];
let webResults = [];

// Process all inputs
allInputs.forEach((input, index) => {
  const data = input.json;
  console.log(`Input ${index}:`, data ? Object.keys(data) : 'null');

  // Query processor data
  if (data && data.searchQuery && data.userId) {
    queryData = data;
    console.log('âœ… Query data found:', data.searchQuery.original);
  }

  // Database results (array of fashion items)
  else if (Array.isArray(data)) {
    console.log('ðŸ—„ï¸ Database results:', data.length);
    dbResults = data.map((item, idx) => {
      const images = parseImages(item.images);
      const discount = item.discount_percentage || 
        (item.original_price && item.price ? 
          Math.round(((item.original_price - item.price) / item.original_price) * 100) : 0);

      return {
        id: item.id || `db_${idx}`,
        title: item.name || 'Fashion Item',
        description: item.description || '',
        image: images,
        price: item.price ? `$${item.price}` : null,
        originalPrice: item.original_price ? `$${item.original_price}` : null,
        brand: item.brand_name || 'Unknown Brand',
        category: item.category_name || 'Fashion',
        colors: item.colors || [],
        sizes: item.sizes || [],
        tags: item.tags || [],
        materials: item.materials || [],
        productCode: item.product_code,
        styleAttributes: item.style_attributes || {},
        source: 'database',
        type: 'fashion_item',
        relevanceScore: calculateDbRelevance(item, queryData?.searchQuery),
        discount: discount,
        isVideo: false,
        popularityScore: item.popularity_score || 0,
        isFeatured: item.is_featured || false
      };
    });
  }

  // Web search results from Bing
  else if (data && data.webPages && data.webPages.value) {
    console.log('ðŸŒ Web results:', data.webPages.value.length);
    webResults = data.webPages.value.map((item, idx) => {
      const title = item.name || 'Fashion Item';
      const description = item.snippet || '';
      const url = item.url || '';

      // Extract potential price from title or description
      const priceMatch = (title + ' ' + description).match(/\$([0-9]+(?:\.[0-9]{2})?)/i);
      const price = priceMatch ? `$${priceMatch[1]}` : null;

      // Try to extract brand from URL or title
      const brandMatch = url.match(/\/\/(?:www\.)?(\w+)\./i);
      const brand = brandMatch ? brandMatch[1].charAt(0).toUpperCase() + brandMatch[1].slice(1) : 'Online Store';

      return {
        id: `web_${idx}`,
        title: title,
        description: description,
        price: price,
        brand: brand,
        image: null, // Web results typically don't have direct image URLs
        url: url,
        source: 'web',
        type: 'web_result',
        relevanceScore: calculateWebRelevance(item, queryData?.searchQuery),
        isVideo: false,
        category: 'Fashion'
      };
    });
  }
});

// Fallback query data
if (!queryData) {
  console.log('âš ï¸ Using fallback query data');
  queryData = {
    searchQuery: { original: 'fashion', expanded: 'fashion clothing', category: 'general', colors: [] },
    userId: 'anonymous',
    maxResults: 20,
    searchId: `fallback_${Date.now()}`
  };
}

// Helper functions
function parseImages(images) {
  if (!images) return null;
  if (typeof images === 'string') {
    try {
      const parsed = JSON.parse(images);
      return Array.isArray(parsed) ? parsed[0] : parsed;
    } catch { return images; }
  }
  return Array.isArray(images) ? images[0] : images;
}

function calculateDbRelevance(item, searchQuery) {
  if (!searchQuery) return 50;

  const query = searchQuery.original.toLowerCase();
  const title = (item.name || '').toLowerCase();
  const description = (item.description || '').toLowerCase();
  const tags = (item.tags || []).join(' ').toLowerCase();
  const brand = (item.brand_name || '').toLowerCase();

  let score = 0;

  // Exact matches (higher weight for database items)
  if (title.includes(query)) score += 50;
  if (description.includes(query)) score += 30;
  if (tags.includes(query)) score += 25;
  if (brand.includes(query)) score += 20;

  // Color matching (exact match since we have structured data)
  if (searchQuery.colors && searchQuery.colors.length > 0) {
    const itemColors = item.colors || [];
    const colorMatches = searchQuery.colors.filter(color => 
      itemColors.some(itemColor => itemColor.toLowerCase().includes(color.toLowerCase()))
    );
    score += colorMatches.length * 30;
  }

  // Category matching
  if (searchQuery.category && searchQuery.category !== 'general') {
    if (item.category_name && item.category_name.toLowerCase().includes(searchQuery.category)) {
      score += 25;
    }
  }

  // Style matching
  if (searchQuery.style && searchQuery.style !== 'general') {
    const styleAttrs = item.style_attributes || {};
    if (Object.values(styleAttrs).some(attr => 
      String(attr).toLowerCase().includes(searchQuery.style)
    )) {
      score += 20;
    }
  }

  // Quality indicators
  if (item.is_featured) score += 15;
  if (item.popularity_score > 0) score += item.popularity_score * 0.1;
  if (item.discount_percentage > 0) score += 5;
  if (item.images) score += 3;

  // Database item bonus (we trust our own data more)
  score += 10;

  return Math.max(score, 1);
}

function calculateWebRelevance(item, searchQuery) {
  if (!searchQuery) return 25;

  const query = searchQuery.original.toLowerCase();
  const title = (item.name || '').toLowerCase();
  const description = (item.snippet || '').toLowerCase();

  let score = 0;

  // Exact matches
  if (title.includes(query)) score += 40;
  if (description.includes(query)) score += 20;

  // Color matching
  if (searchQuery.colors && searchQuery.colors.length > 0) {
    searchQuery.colors.forEach(color => {
      if (title.includes(color) || description.includes(color)) {
        score += 25;
      }
    });
  }

  // Category matching
  if (searchQuery.category && searchQuery.category !== 'general') {
    if (title.includes(searchQuery.category) || description.includes(searchQuery.category)) {
      score += 20;
    }
  }

  // Word matching
  const queryWords = query.split(' ').filter(w => w.length > 2);
  queryWords.forEach(word => {
    if (title.includes(word)) score += 10;
    if (description.includes(word)) score += 5;
  });

  // Fashion-specific bonus
  const fashionTerms = ['fashion', 'style', 'clothing', 'apparel', 'outfit', 'wear', 'shop', 'store'];
  fashionTerms.forEach(term => {
    if (title.includes(term) || description.includes(term)) {
      score += 3;
    }
  });

  return Math.max(score, 1);
}

// Combine and rank results intelligently
let combinedResults = [...dbResults, ...webResults];

// Apply search intent-based adjustments
if (queryData.searchQuery.intent === 'trending') {
  // Boost popular and featured items
  combinedResults = combinedResults.map(item => ({
    ...item,
    relevanceScore: item.relevanceScore + (item.popularityScore || 0) * 0.2 + (item.isFeatured ? 10 : 0)
  }));
} else if (queryData.searchQuery.intent === 'similar') {
  // Boost items with similar attributes
  combinedResults = combinedResults.map(item => ({
    ...item,
    relevanceScore: item.relevanceScore + (item.colors?.length || 0) * 2
  }));
}

// Sort by relevance score
combinedResults.sort((a, b) => b.relevanceScore - a.relevanceScore);

// Limit results and ensure diversity
combinedResults = combinedResults.slice(0, queryData.maxResults || 20);

// Log search analytics
const searchAnalytics = {
  searchId: queryData.searchId,
  userId: queryData.userId,
  query: queryData.searchQuery.original,
  intent: queryData.searchQuery.intent,
  colors: queryData.searchQuery.colors,
  category: queryData.searchQuery.category,
  style: queryData.searchQuery.style,
  resultsCount: combinedResults.length,
  dbResultsCount: dbResults.length,
  webResultsCount: webResults.length,
  topRelevanceScore: combinedResults[0]?.relevanceScore || 0,
  timestamp: new Date().toISOString()
};

console.log('ðŸ“Š Search Analytics:', searchAnalytics);
console.log('ðŸ“ˆ Final results:', combinedResults.length);
console.log('ðŸ† Top 3 results:', combinedResults.slice(0, 3).map(r => ({ 
  title: r.title, 
  score: r.relevanceScore, 
  source: r.source 
})));

// Build comprehensive response
const finalResult = {
  success: true,
  query: queryData.searchQuery,
  results: combinedResults,
  metadata: {
    total: combinedResults.length,
    dbResults: dbResults.length,
    webResults: webResults.length,
    sources: [],
    searchId: queryData.searchId,
    timestamp: new Date().toISOString(),
    filters: queryData.filters || {},
    analytics: searchAnalytics
  },
  debug: {
    inputsProcessed: allInputs.length,
    queryFound: !!queryData,
    searchIntent: queryData.searchQuery.intent,
    enhancedQuery: queryData.searchQuery.expanded,
    topScores: combinedResults.slice(0, 5).map(r => r.relevanceScore)
  }
};

// Add sources
if (dbResults.length > 0) finalResult.metadata.sources.push('database');
if (webResults.length > 0) finalResult.metadata.sources.push('web');

console.log('ðŸŽ¯ SUCCESS - Query:', finalResult.query.original);
console.log('ðŸŽ¯ SUCCESS - Results:', finalResult.results.length);
console.log('ðŸŽ¯ SUCCESS - Sources:', finalResult.metadata.sources);
console.log('=== SMART RESULTS AGGREGATOR END ===');

return finalResult;
